var keyword_mapping = {
  "football": ["teams", "ball", "goal"],
  "formula1": ["car", "racing", "driver"]
};

var setEnum = function(field) {
  var ed = jseditor.getEditor(field);
  if (!ed) return;

  var current_sport = ed.getValue();
  var fc = ed.parent;

  fc.original_schema.properties.keywords["enum"] = keyword_mapping[current_sport];
  fc.schema.properties.keywords["enum"] = keyword_mapping[current_sport];

  fc.removeObjectProperty('keywords');
  //delete the cache
  delete fc.cached_editors.keywords;

  fc.addObjectProperty('keywords');
}

jseditor.on('ready', function() {

  var field = 'root.sport';
  setEnum(field);

  jseditor.watch(field, function() {

    setEnum(field);

  });

});
