var keyword_mapping = {
  "football": {
    "title": "Football label",
    "enum": ["teams", "ball", "goal"],
    "enum_titles": ["Team", "Ball", "Goal"],
  },
  "formula1": {
    "enum": ["car", "racing", "driver"],
  },
  "car": {
    "title": "Car Model",
    "enum": ["audi", "mercedes", "toyota"],
  },
  "driver": {
    "title": "F1 Driver",
    "enum": ["magnussen", "senna", "hamilton"]
  },
  "teams": {
    "title": "Football Team",
    "enum": ["tottenham", "barcelona", "manchester united"]
  },
  "ball": {
    "enum": ["football_", "tennisball", "handball"]
  },
  "magnussen": {
    "enum": ["jan", "kevin"]
  }

};

var chainedFields = ['step1', 'step2', 'step3', 'step4'];

var createFieldsChain = function(fields, mapping) {

  var updateChain = function(currentField, nextField) {
    var ed = jseditor.getEditor('root.' + currentField);
    if (!ed) return;

    var val = ed.getValue(),
    parent = ed.parent,
    map = mapping[val];

    if (map !== undefined) {
      var title = map.title || val,
      enu = map["enum"],
      enum_titles = map.enum_titles || enu,
      poSchema = parent.original_schema.properties[nextField],
      pSchema = parent.schema.properties[nextField];

      poSchema.title = pSchema.title = title;
      poSchema["enum"] = pSchema["enum"] = enu;

      if (!poSchema.options) {
        poSchema.options = pSchema.options = {};
      }
      poSchema.options.enum_titles = pSchema.options.enum_titles = enum_titles;
    }

    parent.removeObjectProperty(nextField);
    //delete the cache
    delete parent.cached_editors[nextField];

    if (val === '' || map === undefined) return;

    parent.addObjectProperty(nextField);

  };

  for (var i = 0; i < fields.length-1; i++) {
    jseditor.watch('root.' + fields[i], updateChain.bind(null, fields[i], fields[i+1]));
  }

};

jseditor.on('ready', function() {
  createFieldsChain(chainedFields, keyword_mapping);
});
